import { createMuiTheme } from "@material-ui/core/styles";

export default createMuiTheme({
  typography: {
    useNextVariants: true
  },
  palette: {
    common: { black: "rgba(0, 237, 254, 1)", white: "#fff" },
    background: {
      paper: "rgba(255, 255, 255, 1)",
      default: "rgba(255, 255, 255, 1)"
    },
    primary: {
      light: "rgba(149, 232, 240, 1)",
      main: "rgba(0, 237, 254, 1)",
      dark: "rgba(0, 180, 195, 1)",
      contrastText: "#fff"
    },
    secondary: {
      light: "rgba(246, 136, 227, 1)",
      main: "rgba(245, 0, 205, 1)",
      dark: "rgba(174, 0, 146, 1)",
      contrastText: "#fff"
    },
    error: {
      light: "#e57373",
      main: "rgba(255, 17, 0, 1)",
      dark: "#d32f2f",
      contrastText: "#fff"
    },
    text: {
      primary: "rgba(0, 0, 0, 1)",
      secondary: "rgba(0, 0, 0, 0.54)",
      disabled: "rgba(0, 0, 0, 0.38)",
      hint: "rgba(0, 0, 0, 0.38)"
    }
  }
});
