import React, { useState } from "react";
import { Route, Link, Redirect } from "react-router-dom";
import { MuiThemeProvider } from "@material-ui/core/styles";
import {
  Toolbar,
  AppBar,
  Menu,
  MenuItem,
  IconButton,
  Typography
} from "@material-ui/core";

import "./App.css";

function HomePage() {
  const [anchorEl, setAnchorEl] = useState(null);
  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  return (
    <MuiThemeProvider theme={theme}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" color="inherit">
            INFO3139 - MaterialUI
          </Typography>
          <IconButton
            onClick={handleClick}
            color="inherit"
            style={{ marginLeft: "auto", paddingRight: "1vh" }}
          >
            <Reorder />
          </IconButton>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem component={Link} to="/home" onClick={handleClose}>
              Home
            </MenuItem>
            <MenuItem component={Link} to="/ex2" onClick={handleClose}>
              Exercise #2
            </MenuItem>
            <MenuItem component={Link} to="/ex3" onClick={handleClose}>
              Exercise #3
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
      <div>
        <Route exact path="/" render={() => <Redirect to="/home" />} />
        <Route path="/ex2" render={() => <MaterialUIEx2 />} />
        <Route path="/ex3" render={() => <MaterialUIEx3 />} />
        <Route path="/home" component={MaterialUIEx5} />
      </div>
    </MuiThemeProvider>
  );
}

export default HomePage;
